export class MaterialCount {
  constructor() {
    this.pawns = 0;
    this.knighs = 0;
    this.bishops = 0;
    this.rooks = 0;
    this.queens = 0;
  }

  clear() {
    this.pawns = 0;
    this.knighs = 0;
    this.bishops = 0;
    this.rooks = 0;
    this.queens = 0;
  }

  score() {
    let score = this.pawns + this.knighs * 3 + this.bishops * 3 + this.rooks * 5 + this.queens * 9;

    if (this.bishops == 2) {
      score += 0.5;
    }

    return score;
  }

  getAllPeaces() {
    return this.knighs + this.bishops + this.rooks + this.queens;
  }

}
